				// checkall checkbox
				$('.chk_all', tbl).off('change.chkall').on('change.chkall', function() {
					//console.log(tbl.find(':checkbox:visible').length);
					$(':checkbox:visible', tbl ).prop('checked',$(this).prop('checked'));
				});

