			image : 			['alt','autofocus','defaultValue','disabled','form','height','name','src','type','value','width'],

			select : 			['disabled','form','multiple','name','size','type','value','_linkedElmID','_linkedElmFilterCol','_linkedElmLabels','_linkedElmOptions'],

